mapper.select:
SELECT EXISTS (
    SELECT 1
    FROM moim_role_tb
    WHERE moim_id = #{moimId} AND user_id = #{userId}
)

mapper.delete:
delete from moim_role_tb
where
    moim_id = #{moimId}
    and user_id = #{userId}

mapper.update:
UPDATE moim_role_tb
SET moim_role = #{moimRole}
WHERE user_id = #{userId} AND moim_id = #{moimId}
